/*jshint esversion: 6 */

let select = document.getElementById("moto_marque");
let modeleSelect = document.getElementById("moto_modele");



select.addEventListener("change", marque_select);


function marque_select(){




        fetch(
          `/double_select/${select.value}`,
          // { credentials: "same-origin" }
          )
        .then(response => response.json())
        .then((data) => {
            let result = "";


            // console.log(data.modeles[0].nom);
        for(var historique of data){


            for (var i = 0; i <= historique.modeles.length; i++) {
                    console.log(historique.modeles[i].nom);
                    result += "<option value='" + historique.modeles[i].nom + "'>" + historique.modeles[i].nom + "</option>";

                }
            $("#moto_modele").html(result);


        }






    });
}
